
public class ternaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i, k;
		
		i = 10;
		k = i < 10 ? -i : i;
		System.out.println("Absolute value of " + i + " is " + k);
		
		i = -10;
		k = i < 10 ? -i : i;
		System.out.println("Absolute value of " + i + " is " + k);
		
	}

}
